CREATE OR REPLACE PROCEDURE same_desfazer_devolver_lote ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_devolvido_w	bigint;


BEGIN

/* ATENÇÃO: ESTA PROCEDURE NÃO TEM COMMIT, POIS SOMENTE É CHAMADA ATRAVÉS DE OUTRA PROCEDURE.
 *          CASO SEJA NECESSÁRIO IMPLEMENTAR O COMMIT, FAVOR VERIFICAR OS PONTOS ONDE PODEM
 *          OCORRER ROLLBACKS E TRATAR OS MESMOS DE OUTRA FORMA.
 */
select	count(*)
into STRICT	qt_devolvido_w
from	same_solic_pront
where	nr_seq_lote	= nr_seq_lote_p
and	(dt_devolucao IS NOT NULL AND dt_devolucao::text <> '');

if (qt_devolvido_w = 0) then
	update	same_solic_pront_lote
	set	ie_status	= 'E',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_lote_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
