SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE same_desmembrar_receb (nr_seq_receb_p bigint, ds_prontuario_p text, nm_usuario_p text) AS $body$
DECLARE


ds_prontuario_w		varchar(400);
ie_contador_w		bigint	:= 0;
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
nr_seq_pront_w		bigint;
nr_seq_receb_w		bigint;


BEGIN

select	nextval('transf_prontuario_seq')
into STRICT	nr_seq_receb_w
;

insert into TRANSF_PRONTUARIO(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_pessoa_origem,
		cd_pessoa_destino,
		cd_setor_origem,
		cd_setor_destino,
		dt_envio,
		dt_recebimento,
		nr_prontuario,
		nr_atendimento,
		cd_pessoa_fisica,
		cd_pessoa_receb,
		ds_observacao,
		dt_periodo_inicial,
		dt_periodo_final,
		cd_estabelecimento,
		nr_seq_tipo)
SELECT		nr_seq_receb_w,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_pessoa_origem,
		cd_pessoa_destino,
		cd_setor_origem,
		cd_setor_destino,
		dt_envio,
		dt_recebimento,
		nr_prontuario,
		nr_atendimento,
		cd_pessoa_fisica,
		cd_pessoa_receb,
		ds_observacao,
		dt_periodo_inicial,
		dt_periodo_final,
		coalesce(cd_estabelecimento,wheb_usuario_pck.get_cd_estabelecimento),
		nr_seq_tipo
from	TRANSF_PRONTUARIO
where	nr_sequencia	= nr_seq_receb_p;

ds_prontuario_w		:= ds_prontuario_p;

while	(ds_prontuario_w IS NOT NULL AND ds_prontuario_w::text <> '') or
	ie_contador_w > 200 loop
	begin
	tam_lista_w		:= length(ds_prontuario_w);
	ie_pos_virgula_w	:= position(',' in ds_prontuario_w);

	if (ie_pos_virgula_w <> 0) then
		nr_seq_pront_w		:= substr(ds_prontuario_w,1,(ie_pos_virgula_w-1));
		ds_prontuario_w		:= substr(ds_prontuario_w,(ie_pos_virgula_w+1),tam_lista_w);
	end if;

	insert into TRANSF_PRONTUARIO_envelope(
				NR_SEQUENCIA,
				DT_ATUALIZACAO,
				NM_USUARIO,
				DT_ATUALIZACAO_NREC,
				NM_USUARIO_NREC,
				NR_SEQ_TRANSF,
				NR_SEQ_PRONTUARIO
	) values (
		nextval('transf_prontuario_envelope_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_receb_w,
		nr_seq_pront_w);
	
	delete	from TRANSF_PRONTUARIO_envelope
	where	nr_seq_TRANSF		= nr_seq_receb_p
	and	nr_seq_prontuario	= nr_seq_pront_w;

	ie_contador_w	:= ie_contador_w + 1;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
