SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE same_devolver_solic ( nr_atendimento_p bigint, nr_prontuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
qt_itens_w		bigint;
cd_pessoa_fisica_w	varchar(10);

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	same_solic_pront
	where	ie_status in ('E','R')
	and	nr_atendimento = nr_atendimento_p
	and	coalesce(dt_devolucao::text, '') = '';

c02 CURSOR FOR
	SELECT	nr_sequencia
	from	same_solic_pront
	where	ie_status in ('E','R')
	and	obter_prontuario_pf(cd_estabelecimento, CD_PESSOA_FISICA) = nr_prontuario_p
	and	coalesce(dt_devolucao::text, '') = ''
	and	coalesce(nr_atendimento::text, '') = '';


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	open c01;
	loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */	
		update	same_solic_pront
		set	ie_status = 'D',
			dt_devolucao = clock_timestamp()
		where	nr_sequencia = nr_sequencia_w;	
	end loop;	
	close c01;
	
end if;

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	pessoa_fisica
where	nr_prontuario = nr_prontuario_p;

select	count(*)
into STRICT	qt_itens_w
from	same_prontuario
where	ie_status = '8'
and	CD_PESSOA_FISICA = cd_pessoa_fisica_w;

if (qt_itens_w = 0) then

	open c02;
	loop
	fetch c02 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

		update	same_solic_pront
		set	ie_status = 'D',
			dt_devolucao = clock_timestamp()
		where	nr_sequencia = nr_sequencia_w;

	end loop;	
	close c02;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
