CREATE OR REPLACE PROCEDURE same_emp_exame_solic ( nr_sequencia_p bigint, nr_prontuario_p bigint, cd_setor_atendimento_p bigint, cd_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_pront_solic_w	smallint;
qt_emprestimo_w		smallint;
nr_sequencia_w		bigint;
nr_seq_w		bigint;
cd_estabelecimento_w	smallint;
cd_setor_atendimento_w	integer;
cd_agenda_w		bigint;


BEGIN

if (nr_sequencia_p <> 0) then
	nr_sequencia_w		:= nr_sequencia_p;
	cd_setor_atendimento_w	:= cd_setor_atendimento_p;
elsif (cd_setor_atendimento_p <> 0) then
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	exame_solicitacao
	where	nr_prontuario		= nr_prontuario_p
	and	cd_setor_atendimento	= cd_setor_atendimento_p
	and	((cd_agenda = cd_agenda_p)
	or (cd_agenda_p = 0))
	and	trunc(dt_desejada,'dd') = trunc(clock_timestamp(),'dd')
	and	ie_status_solic		= 'A';
	cd_setor_atendimento_w := cd_setor_atendimento_p;
	end;
else
	select	coalesce(MAX(nr_sequencia),0),
		coalesce(MAX(cd_setor_atendimento),0)
	into STRICT	nr_sequencia_w,
		cd_setor_atendimento_w
	from	exame_solicitacao
	where	nr_prontuario		= nr_prontuario_p
	and	ie_status_solic		= 'A'
	and	dt_desejada		= (	SELECT	MIN(dt_desejada)
						from	exame_solicitacao
						where	nr_prontuario	= nr_prontuario_p
						and	ie_status_solic 	= 'A'
						and	trunc(dt_desejada) >= trunc(clock_timestamp()));
end if;

if (nr_sequencia_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(282030);
end if;

select	cd_estabelecimento,
	cd_agenda
into STRICT	cd_estabelecimento_w,
	cd_agenda_w
from	exame_solicitacao
where	nr_sequencia		= nr_sequencia_w;

select	count(*)
into STRICT	qt_emprestimo_w
from	exame_emprestimo
where	nr_prontuario		= nr_prontuario_p
and	coalesce(dt_devolucao::text, '') = '';

if (qt_emprestimo_w <> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(282031);
end if;

select	nextval('exame_emprestimo_seq')
into STRICT	nr_seq_w
;

insert into exame_emprestimo(
	nr_sequencia,
	cd_estabelecimento,
	nr_prontuario,
	dt_atualizacao,
	nm_usuario,
	cd_setor_atendimento,
	dt_emprestimo,
	nm_usuario_empr,
	ie_operacao,
	cd_agenda,
	nr_seq_solic)
Values (
	nr_seq_w,
	cd_estabelecimento_w,
	nr_prontuario_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_setor_atendimento_w,
	clock_timestamp(),
	nm_usuario_p,
	'E',
	cd_agenda_w,
	nr_sequencia_w);

update	exame_solicitacao
set	ie_status_solic = 	'T',
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_sequencia_w;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
