CREATE OR REPLACE PROCEDURE same_gerar_historico (nr_seq_prontuario_p bigint, nr_seq_operacao_p bigint, cd_pessoa_fisica_p text, cd_setor_solicitante_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* ATENÇÃO: ESTA PROCEDURE NÃO TEM COMMIT, POIS SOMENTE É CHAMADA ATRAVÉS DE OUTRA PROCEDURE.
 *          CASO SEJA NECESSÁRIO IMPLEMENTAR O COMMIT, FAVOR VERIFICAR OS PONTOS ONDE PODEM
