 */
nr_seq_caixa_w		bigint;
nr_seq_local_w		bigint;


BEGIN

select	max(nr_seq_caixa),
	max(nr_seq_local)
into STRICT	nr_seq_caixa_w,
	nr_seq_local_w
from	same_prontuario
where	nr_sequencia	= nr_seq_prontuario_p;

insert into same_prontuario_hist(
	nr_sequencia,
	nr_seq_same,
	dt_atualizacao,
	nm_usuario,
	nr_seq_operacao,
	dt_historico,
	nr_seq_caixa,
	nr_seq_local,
	cd_pessoa_fisica,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_setor_solicitante
) values (
	nextval('same_prontuario_hist_seq'),
	nr_seq_prontuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_operacao_p,
	clock_timestamp(),
	nr_seq_caixa_w,
	nr_seq_local_w,
	cd_pessoa_fisica_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_setor_solicitante_p
);

