CREATE OR REPLACE PROCEDURE same_gravar_sequencia ( nr_seq_local_p bigint, cd_caixa_ini_p text, cd_caixa_fim_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

cd_caixa_w	varchar(20);
nr_sequencia_w	bigint;
nr_seq_existe_w	bigint;
cd_caixa_ini_w	bigint;
cd_caixa_fim_w	bigint;

BEGIN
	cd_caixa_ini_w := cd_caixa_ini_p;
	cd_caixa_fim_w := cd_caixa_fim_p;
	while cd_caixa_ini_w <= cd_caixa_fim_w loop
		cd_caixa_w := cd_caixa_ini_w;
		
		select max(nr_sequencia)
		into STRICT nr_seq_existe_w
		from same_caixa
		where cd_caixa = cd_caixa_w
		and nr_seq_local = nr_seq_local_p;
		
		if (coalesce(nr_seq_existe_w::text, '') = '') then
			begin
				select	nextval('same_caixa_seq')
				into STRICT	nr_sequencia_w
				;		
				
				insert into	same_caixa(	nr_sequencia,
					nr_seq_local,
					cd_caixa,
					cd_estabelecimento,
					ie_situacao,
					nm_usuario,			
					dt_atualizacao
				)
				values (	nr_sequencia_w,
					nr_seq_local_p,
					cd_caixa_w,
					cd_estabelecimento_p,
					'A',
					nm_usuario_p,
					clock_timestamp()
				);
			end;
		else
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1075830);
		end if;
		cd_caixa_ini_w := cd_caixa_ini_w + 1;
	end loop;
if (coalesce(nr_seq_existe_w::text, '') = '') then
	commit;
end if;

end;
$body$
