CREATE OR REPLACE PROCEDURE same_inserir_atend_lote (cd_pessoa_fisica_p text, nr_seq_lote_p bigint) AS $body$
DECLARE

 
nr_atendimento_w		bigint;
nr_sequencia_w		bigint;
cd_pessoa_solicitante_w	varchar(10);
ie_status_w			varchar(01);
dt_prevista_w			timestamp;
dt_entrega_w			timestamp;
ie_tipo_solicitacao_w	varchar(01);
ie_urgente_w			varchar(01);
nr_seq_motivo_w		bigint;
cd_estabelecimento_w		smallint;
nm_usuario_w			varchar(15);

 
 
C01 CURSOR FOR 
	SELECT	distinct nr_atendimento 
	from	atendimento_paciente 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;


BEGIN 
 
if (nr_seq_lote_p > 0) then 
 
	select	cd_estabelecimento, 
		nm_usuario, 
		ie_status, 
		dt_prevista, 
		dt_entrega, 
		cd_pessoa_solicitante, 
		ie_tipo_solicitacao, 
		ie_urgente, 
		nr_seq_motivo 
	into STRICT	cd_estabelecimento_w, 
		nm_usuario_w, 
		ie_status_w, 
		dt_prevista_w, 
		dt_entrega_w, 
		cd_pessoa_solicitante_w, 
		ie_tipo_solicitacao_w, 
		ie_urgente_w, 
		nr_seq_motivo_w 
	from	same_solic_pront_lote 
	where	nr_sequencia = nr_seq_lote_p;
 
	OPEN C01;
	LOOP 
		FETCH C01 into 
			nr_atendimento_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
 
		Select	nextval('same_solic_pront_seq') 
		into STRICT	nr_sequencia_w 
		;
 
 
		insert into same_solic_pront(	nr_sequencia, 
						cd_estabelecimento, 
						dt_solicitacao,	 
						dt_atualizacao, 
						nm_usuario, 
						ie_status, 
						cd_pessoa_fisica, 
						nr_atendimento, 
						dt_prevista, 
						dt_entrega, 
						ds_observacao, 
						cd_pessoa_solicitante, 
						ie_tipo_solicitacao, 
						ie_urgente, 
						nr_seq_lote, 
						nr_seq_motivo) 
				values (	nr_sequencia_w, 
						cd_estabelecimento_w, 
						clock_timestamp(), 
						clock_timestamp(), 
						nm_usuario_w, 
						ie_status_w, 
						cd_pessoa_fisica_p, 
						nr_atendimento_w, 
						dt_prevista_w, 
						dt_entrega_w, 
						' ', 
						cd_pessoa_solicitante_w, 
						ie_status_w, 
						ie_urgente_w, 
						nr_seq_lote_p, 
						nr_seq_motivo_w);
		end loop;
	close c01;
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
