CREATE OR REPLACE PROCEDURE same_obter_alerta_dev (cd_pessoa_fisica_p text, nr_horas_p bigint, ie_possui_alerta_p INOUT text, nm_paciente_p INOUT text, ds_agenda_p INOUT text) AS $body$
DECLARE

 
ie_possui_alerta_w	varchar(1);		
ds_agenda_w		varchar(80);
nm_paciente_w		varchar(255);	
 
			 

BEGIN 
ie_possui_alerta_p := 'N';
 
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
into STRICT	ie_possui_alerta_w 
from	agenda_consulta 
where	cd_pessoa_fisica = cd_pessoa_fisica_p 
and	dt_agenda between clock_timestamp() and clock_timestamp() + nr_horas_p/24;
 
if (ie_possui_alerta_w = 'S') then 
	ie_possui_alerta_p := 'S';
	 
	select	max(obter_nome_agenda(cd_agenda)), 
		max(substr(obter_nome_pf(cd_pessoa_fisica),1,255)) 
	into STRICT	ds_agenda_w, 
		nm_paciente_w 
	from	agenda_consulta 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	dt_agenda between clock_timestamp() and clock_timestamp() + nr_horas_p/24;
	 
	nm_paciente_p := nm_paciente_w;
	ds_agenda_p := ds_agenda_w;
		 
end if;
 
	 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
