CREATE OR REPLACE PROCEDURE same_ordernar_volume_pront ( cd_pessoa_fisica_p text, ie_opcao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_sequencia_pront_w	bigint;
dt_periodo_inicial_w	timestamp;
nr_contador_w		bigint;
ds_parametro_w		varchar(10) := obter_valor_param_usuario(941,103,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p);

/*
ie_opcao_p
	0 - ordena pela data e sequÃªncia
*/
c01 CURSOR FOR
SELECT 	nr_sequencia,
	dt_periodo_inicial
from	same_prontuario
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	ie_status    <> '2'
and	((ds_parametro_w = 'N') or (cd_estabelecimento = cd_estabelecimento_p))
order by dt_periodo_inicial, nr_sequencia;


BEGIN

nr_contador_w	:= 0;

open c01;
loop
fetch c01 into 	nr_sequencia_pront_w,
		dt_periodo_inicial_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	nr_contador_w	:= nr_contador_w + 1;

	update 	same_prontuario
	set	nr_seq_volume	= nr_contador_w,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_pront_w;

	end;
end loop;
close c01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
