SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE same_receber_prontuario ( ds_prontuario_p text, ie_forma_controle_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
ds_prontuario_w			varchar(2000);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nr_seq_pront_w			bigint;
cd_pessoa_fisica_w		varchar(10);
cd_setor_atendimento_w		integer;
nr_seq_solic_pront_w		bigint;
nr_atendimento_w		bigint;
cd_pessoa_pront_w		varchar(10);
ds_parametro_w			varchar(10) := obter_valor_param_usuario(941,103,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p);
nr_seq_same_pront_w		bigint;
cd_pessoa_solicitante_w		varchar(10);
cd_setor_solicitante_w		integer;
nr_seq_pront_env_w		same_solic_pront_envelope.nr_seq_prontuario%type;

c01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	same_solic_pront 
	where	((nr_atendimento = nr_atendimento_w) or ((coalesce(nr_atendimento::text, '') = '') and (cd_pessoa_fisica = cd_pessoa_pront_w))) 
	and		coalesce(dt_recebimento::text, '') = '' 
	and		ie_status in ('E','R') 
	and	((ds_parametro_w = 'N') or (cd_estabelecimento = cd_estabelecimento_p));

c02 CURSOR FOR 
	SELECT	nr_sequencia 
	from	same_solic_pront 
	where	cd_pessoa_fisica = cd_pessoa_pront_w 
	and		coalesce(dt_recebimento::text, '') = '' 
	and		ie_status in ('E','R') 
	and	((ds_parametro_w = 'N') or (cd_estabelecimento = cd_estabelecimento_p));
	
C03 CURSOR FOR 
	SELECT	nr_sequencia 
	from	same_prontuario 
	where (nr_atendimento		= nr_atendimento_w or coalesce(nr_atendimento_w::text, '') = '') 
	and	cd_pessoa_fisica	= cd_pessoa_pront_w 
	and	((ds_parametro_w = 'N') or (cd_estabelecimento = cd_estabelecimento_p)) 
	order by nr_atendimento;
	

BEGIN 
 
ds_prontuario_w		:= ds_prontuario_p;
 
select	substr(obter_pessoa_fisica_usuario(nm_usuario_p, 'C'),1,10), 
	obter_setor_usuario(nm_usuario_p) 
into STRICT	cd_pessoa_fisica_w, 
	cd_setor_atendimento_w
;
 
while	(ds_prontuario_w IS NOT NULL AND ds_prontuario_w::text <> '') or ie_contador_w > 200 loop 
	begin 
	tam_lista_w		:= length(ds_prontuario_w);
	ie_pos_virgula_w	:= position(',' in ds_prontuario_w);
 
	if (ie_pos_virgula_w <> 0) then 
		nr_seq_pront_w		:= substr(ds_prontuario_w,1,(ie_pos_virgula_w-1));
		ds_prontuario_w		:= substr(ds_prontuario_w,(ie_pos_virgula_w+1),tam_lista_w);
	end if;
 
	if (ie_forma_controle_p = 'N') then 
 
		select max(nr_atendimento) 
		into STRICT nr_atendimento_w 
		from same_prontuario 
		where nr_sequencia = nr_seq_pront_w;
		 
		select max(cd_pessoa_fisica) 
		into STRICT cd_pessoa_pront_w 
		from same_prontuario 
		where nr_sequencia = nr_seq_pront_w;
		 
		if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
			OPEN C01;
				LOOP 
				FETCH C01 into 
					nr_seq_solic_pront_w;
				EXIT WHEN NOT FOUND; /* apply on c01 */
 
				begin 
				update	same_solic_pront 
				set	dt_recebimento	= clock_timestamp(), 
					dt_recebimento_setor = clock_timestamp(), 
					nm_usuario_receb_setor = nm_usuario_p, 
					nm_usuario_receb = nm_usuario_p, 
					dt_atualizacao	= clock_timestamp(), 
					nm_usuario	= nm_usuario_p, 
					ie_status	= 'R' 
				where	nr_sequencia	= nr_seq_solic_pront_w 
				and	coalesce(dt_recebimento::text, '') = '';
				 
				select	max(a.cd_pessoa_solicitante), 
						max(a.cd_setor_solicitante) 
				into STRICT	cd_pessoa_solicitante_w, 
						cd_setor_solicitante_w 
				from	same_solic_pront a 
				where	a.nr_sequencia = nr_seq_solic_pront_w;
				 
					open C03;
					loop 
					fetch C03 into	 
						nr_seq_same_pront_w;
						EXIT WHEN NOT FOUND; /* apply on C03 */
						begin 
						CALL Same_Gerar_Historico(	nr_seq_same_pront_w, 
								38, 
								cd_pessoa_solicitante_w, 
								cd_setor_solicitante_w, 
								nm_usuario_p);
						end;
					end loop;
					close C03;
				 
				end;
				END LOOP;
				CLOSE C01;
				 
		elsif (cd_pessoa_pront_w IS NOT NULL AND cd_pessoa_pront_w::text <> '') then 
			OPEN C02;
				LOOP 
				FETCH C02 into 
					nr_seq_solic_pront_w;
				EXIT WHEN NOT FOUND; /* apply on c02 */
 
				begin 
				update	same_solic_pront 
				set	dt_recebimento	= clock_timestamp(), 
					dt_recebimento_setor = clock_timestamp(), 
					nm_usuario_receb_setor = nm_usuario_p, 
					nm_usuario_receb = nm_usuario_p, 
					dt_atualizacao	= clock_timestamp(), 
					nm_usuario	= nm_usuario_p, 
					ie_status	= 'R' 
				where	nr_sequencia	= nr_seq_solic_pront_w 
				and	coalesce(dt_recebimento::text, '') = '';
				 
				select	max(a.cd_pessoa_solicitante), 
					max(a.cd_setor_solicitante) 
				into STRICT	 
					cd_pessoa_solicitante_w, 
					cd_setor_solicitante_w 
				from	same_solic_pront a 
				where	a.nr_sequencia = nr_seq_solic_pront_w;
				 
				open C03;
				loop 
				fetch C03 into	 
					nr_seq_same_pront_w;
					EXIT WHEN NOT FOUND; /* apply on C03 */
					begin 
					CALL Same_Gerar_Historico(	nr_seq_same_pront_w, 
							38, 
							cd_pessoa_solicitante_w, 
							cd_setor_solicitante_w, 
							nm_usuario_p);
					end;
				end loop;
				close C03;
					 
				end;
				END LOOP;
				CLOSE C02;	
		end if;
		 
	else 
		select	max(b.nr_sequencia), 
			max(b.nr_seq_prontuario) 
		into STRICT	nr_seq_solic_pront_w, 
			nr_seq_pront_env_w 
		from	same_solic_pront a, 
			same_solic_pront_envelope b 
		where	a.nr_sequencia		= b.nr_seq_solic 
		and	b.nr_seq_prontuario	= nr_seq_pront_w 
		and	a.ie_status		in ('E','R') 
		and	coalesce(b.dt_recebimento::text, '') = '';
 
		update	same_solic_pront_envelope 
		set	dt_recebimento	= clock_timestamp(), 
			cd_pessoa_receb	= cd_pessoa_fisica_w, 
			dt_atualizacao	= clock_timestamp(), 
			nm_usuario	= nm_usuario_p 
		where	nr_sequencia	= nr_seq_solic_pront_w 
		and	coalesce(dt_recebimento::text, '') = '';
		 
		if (nr_seq_pront_env_w IS NOT NULL AND nr_seq_pront_env_w::text <> '') then 
			CALL Same_Gerar_Historico(	nr_seq_pront_env_w, 
									38, 
									cd_pessoa_fisica_w, 
									obter_setor_ativo, 
									nm_usuario_p);
		end if;
	end if;
	ie_contador_w	:= ie_contador_w + 1;
 
	end;
end loop;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE same_receber_prontuario ( ds_prontuario_p text, ie_forma_controle_p text, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

