CREATE OR REPLACE PROCEDURE same_solic_pront_insere ( nr_seq_lote_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, cd_pessoa_solicitante_p text, cd_setor_solicitante_p bigint, nr_atendimento_p bigint, dt_periodo_inicial_p timestamp, dt_periodo_final_p timestamp, nm_usuario_p text) AS $body$
DECLARE

nr_seq_motivo_w		bigint;
qt_itens_w		bigint;
nr_prontuario_w		bigint;
dt_devolucao_prevista_w	timestamp;
ds_parametro_w		varchar(10) := obter_valor_param_usuario(941,103,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p);


BEGIN


select	max(nr_seq_motivo),
	max(dt_devolucao_prevista)
into STRICT	nr_seq_motivo_w,
	dt_devolucao_prevista_w
from	same_solic_pront_lote
where	nr_sequencia = nr_seq_lote_p;

nr_prontuario_w	:= obter_prontuario_pf(cd_estabelecimento_p,cd_pessoa_fisica_p);

select 	count(*)
into STRICT	qt_itens_w
from	same_prontuario
where	cd_pessoa_fisica 	= cd_pessoa_fisica_p
and	((nr_atendimento = coalesce(nr_Atendimento_p,0)) or (coalesce(nr_Atendimento_p,0) = 0))
and	((ds_parametro_w = 'N') or (cd_estabelecimento = cd_estabelecimento_p));


if (qt_itens_w = 0) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort( 264213 , 'NR_PRONTUARIO_W='||nr_prontuario_w);
end if;


insert into same_solic_pront(
	nr_sequencia,
	cd_estabelecimento,
	dt_solicitacao,
	dt_atualizacao,
	nm_usuario,
	ie_status,
	cd_pessoa_fisica,
	cd_pessoa_solicitante,
	ie_tipo_solicitacao,
	ie_urgente,
	nr_seq_lote,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_setor_solicitante,
	nr_atendimento,
	dt_periodo_inicial,
	dt_periodo_final,
	dt_devolucao_prevista,
	nr_seq_motivo
) values (
	nextval('same_solic_pront_seq'),
	cd_estabelecimento_p,
	clock_timestamp(),
	clock_timestamp(),
	nm_usuario_p,
	'P',
	cd_pessoa_fisica_p,
	cd_pessoa_solicitante_p,
	'O',
	'N',
	nr_seq_lote_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_setor_solicitante_p,
	nr_atendimento_p,
	dt_periodo_inicial_p,
	dt_periodo_final_p,
	dt_devolucao_prevista_w,
	nr_seq_motivo_w);

commit;

end;
$body$
