CREATE OR REPLACE PROCEDURE san_altera_sangria ( ie_opcao_p text, nr_seq_sangria_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (nr_seq_sangria_p IS NOT NULL AND nr_seq_sangria_p::text <> '') then
	if (ie_opcao_p = 'I') then
		update	SAN_SANGRIA_TERAPEUTICA
		set	dt_inicio = clock_timestamp(),
			nm_usuario_inicio = nm_usuario_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_sangria_p;
	end if;

	if (ie_opcao_p = 'F') then
		update	SAN_SANGRIA_TERAPEUTICA
		set	dt_fim_sangria = clock_timestamp(),
			nm_usuario_fim = nm_usuario_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_sangria_p;
	end if;

end if;

commit;

end;
