CREATE OR REPLACE PROCEDURE san_altera_status_emp_estab (nr_seq_emp_estab_p bigint, ie_status_emp_p text) AS $body$
DECLARE


nr_seq_emp_estab_hem_w	bigint;
cd_estab_destino_w	smallint;
nr_seq_producao_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_producao
	from	san_emp_estab_hem
	where	nr_seq_emp_estab = nr_seq_emp_estab_p
	order by nr_sequencia;

BEGIN
if (nr_seq_emp_estab_p IS NOT NULL AND nr_seq_emp_estab_p::text <> '') and (ie_status_emp_p IS NOT NULL AND ie_status_emp_p::text <> '') then


	select	max(cd_estab_destino)
	into STRICT	cd_estab_destino_w
	from	san_emp_estab
	where	nr_sequencia = nr_seq_emp_estab_p;

	update	san_emp_estab
	set	ie_status_emp	= ie_status_emp_p
	where	nr_sequencia	= nr_seq_emp_estab_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_emp_estab_hem_w,
		nr_seq_producao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	san_emp_estab_hem
		set	ie_status = ie_status_emp_p
		where	nr_sequencia = nr_seq_emp_estab_hem_w;

		if (ie_status_emp_p = 'R') then
			update	san_producao
			set	cd_estab_atual = cd_estab_destino_w,
				ie_encaminhado = 'N'
			where	nr_sequencia = nr_seq_producao_w;

		end if;

		if (ie_status_emp_p = 'E') then
			update	san_producao
			set	ie_encaminhado = 'S'
			where	nr_sequencia = nr_seq_producao_w;
		end if;
		end;
	end loop;
	close C01;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
