CREATE OR REPLACE PROCEDURE san_altera_texto_envio_corresp (nr_seq_envio_p bigint, nr_seq_texto_padrao_p bigint) AS $body$
DECLARE


nr_seq_envio_w		bigint;
nr_seq_corresp_item_w	bigint;
ds_texto_padrao_w		text;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	san_envio_corresp_item a
	where	a.nr_seq_envio = nr_seq_envio_p;


BEGIN
if (nr_seq_envio_p IS NOT NULL AND nr_seq_envio_p::text <> '') then

	select 	ds_texto
	into STRICT	ds_texto_padrao_w
	from	san_texto_padrao
	where 	nr_sequencia  = nr_seq_texto_padrao_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_envio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		update	san_envio_corresp_item
		set	ds_texto_padrao	= ds_texto_padrao_w
		where	nr_seq_envio	= nr_seq_envio_p;
		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
