CREATE OR REPLACE PROCEDURE san_armazernar_bolsa ( nr_seq_producao_p bigint, nr_seq_local_armaz_p bigint, nr_seq_armazenamento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_congelacao_w			bigint;

  reg_congelacao RECORD;

BEGIN
if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then
	update	san_producao x
	set	x.nr_seq_local_armaz		= nr_seq_local_armaz_p,
		nr_seq_armazenamento		= nr_seq_armazenamento_p,
		x.nm_usuario_local_armaz	= nm_usuario_p,
		x.nm_usuario			= nm_usuario_p,
		x.dt_local_armaz		= clock_timestamp(),
		x.dt_utilizacao			= clock_timestamp()
	where	x.nr_sequencia			= nr_seq_producao_p;

	select	count(1)
	into STRICT	qt_congelacao_w
	from	san_dados_congelacao	a
	where	a.nr_seq_producao	= nr_seq_producao_p;

	if (qt_congelacao_w > 0) then
		for reg_congelacao in (	SELECT	a.nr_sequencia
					from	san_dados_congelacao	a
					where	a.nr_seq_producao	= nr_seq_producao_p) loop
			begin
			CALL san_liberar_crioprotetores(	reg_congelacao.nr_sequencia,
							obter_estabelecimento_ativo,
							nm_usuario_p);
			end;
		end loop;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
