CREATE OR REPLACE PROCEDURE san_atualizar_conf_exame ( nr_seq_exame_p bigint, nr_seq_exame_lote_p bigint, ds_resultado_p text, ie_conferencia_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') then

	if ( ie_conferencia_p = 'P') then
		update	san_exame_realizado
		set	dt_conferencia		= clock_timestamp(),
			nm_usuario_conferencia 	= nm_usuario_p,
			ds_resultado_prim_conf	= ds_resultado_p
		where	nr_seq_exame		= nr_seq_exame_p
		and	nr_seq_exame_lote	= nr_seq_exame_lote_p;
	else
		update	san_exame_realizado
		set	dt_conferencia_seg     	= clock_timestamp(),
			nm_usuario_conf_seg    	= nm_usuario_p,
			ds_resultado_seg_conf	= ds_resultado_p
		where	nr_seq_exame		= nr_seq_exame_p
		and	nr_seq_exame_lote	= nr_seq_exame_lote_p;
	end if;

end if;

commit;

end;
$body$
