CREATE OR REPLACE PROCEDURE san_atualizar_dados_amostra ( nr_seq_doacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_doacao_amostra_w	bigint;
qt_peso_w		real;
qt_altura_w		real;
qt_temperatura_w	real;
pr_hematocrito_w	double precision;


BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	select	max(nr_seq_doacao_amostra)
	into STRICT	nr_seq_doacao_amostra_w
	from	san_doacao
	where	nr_sequencia = nr_seq_doacao_p;

	select	max(qt_peso),
		max(qt_altura),
		max(qt_temperatura),
		max(pr_hematocrito)
	into STRICT	qt_peso_w,
		qt_altura_w,
		qt_temperatura_w,
		pr_hematocrito_w
	from	san_doacao
	where	nr_sequencia = nr_seq_doacao_amostra_w;

	update	san_doacao
	set	qt_peso		= qt_peso_w,
		qt_altura	= qt_altura_w,
		qt_temperatura	= qt_temperatura_w,
		pr_hematocrito	= pr_hematocrito_w
	where	nr_sequencia = nr_seq_doacao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
