CREATE OR REPLACE PROCEDURE san_atualizar_dados_doador (nr_seq_doacao_p bigint) AS $body$
DECLARE

 
qt_peso_w 		double precision;
qt_altura_cm_w 		double precision;
cd_pessoa_fisica_w	varchar(10);


BEGIN 
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then 
 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	san_doacao 
	where	nr_sequencia = nr_seq_doacao_p;
 
	select	max(qt_peso) 
	into STRICT 	qt_peso_w 
	from	atendimento_sinal_vital 
	where	nr_sequencia =(SELECT	coalesce(max(c.nr_sequencia),-1) 
				from	atendimento_sinal_vital c, 
					atendimento_paciente b 
				where	(c.qt_peso IS NOT NULL AND c.qt_peso::text <> '') 
				and	c.nr_atendimento	= b.nr_atendimento 
				and	b.cd_pessoa_fisica 	= cd_pessoa_fisica_w 
				and	c.ie_situacao		= 'A' 
				and	coalesce(c.ie_rn,'N')	= 'N');
 
	select	max(qt_altura_cm) 
	into STRICT 	qt_altura_cm_w 
	from	atendimento_sinal_vital			 
	where	nr_sequencia =(SELECT	coalesce(max(c.nr_sequencia),-1) 
				from	atendimento_sinal_vital c, 
					atendimento_paciente b 
				where	(c.qt_altura_cm IS NOT NULL AND c.qt_altura_cm::text <> '') 
				and	c.nr_atendimento	= b.nr_atendimento 
				and	b.cd_pessoa_fisica 	= cd_pessoa_fisica_w 
				and	c.ie_situacao		= 'A' 
				and	coalesce(c.ie_rn,'N')	= 'N');
 
 
	update	san_doacao 
	set	qt_peso 	= qt_peso_w, 
		qt_altura 	= coalesce(qt_altura_cm_w, obter_qt_altura_pf(cd_pessoa_fisica_w)) 
	where	nr_sequencia 	= nr_seq_doacao_p;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE san_atualizar_dados_doador (nr_seq_doacao_p bigint) FROM PUBLIC;

