CREATE OR REPLACE PROCEDURE san_atualizar_filtragem ( nr_seq_doacao_p bigint, data_filtragem_p timestamp, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_filtrar_hemo_bolsa_w	varchar(1);


BEGIN

ie_filtrar_hemo_bolsa_w	:= coalesce(obter_valor_param_usuario(450,270,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p),'S');

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	if (ie_opcao_p = 'I') then

		update 	san_doacao
		set	dt_inicio_filtragem 	 	= data_filtragem_p,
			nm_usuario_inicio_filtragem 	= nm_usuario_p
		where 	nr_sequencia		 	= nr_seq_doacao_p;

	else
		update 	san_doacao
		set	dt_fim_filtragem 		= data_filtragem_p,
			nm_usuario_fim_filtragem 	= nm_usuario_p
		where 	nr_sequencia			= nr_seq_doacao_p;

		if (ie_filtrar_hemo_bolsa_w = 'S') then
			update 	san_producao
			set	ie_filtrado 	= 'S'
			where 	nr_seq_doacao	= nr_seq_doacao_p;
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
