SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_atualizar_imped_exame ( nr_seq_doacao_p bigint, nr_seq_exame_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_impedimento_w	bigint;
ie_novo_questionario_w	varchar(1) := 'N';

BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	Select 	max(ie_novo_questionario)
	into STRICT	ie_novo_questionario_w
	from 	san_parametro
	where 	cd_estabelecimento = cd_estabelecimento_p;


	select	max(nr_seq_impedimento)
	into STRICT	nr_seq_impedimento_w
	from 	san_exame
	where 	nr_sequencia = nr_seq_exame_p;

	if (nr_seq_impedimento_w IS NOT NULL AND nr_seq_impedimento_w::text <> '') then

		if (ie_novo_questionario_w = 'S') then

			update	san_questionario
			set	ie_impede_doacao = 'N'
			where 	nr_seq_doacao 		= nr_seq_doacao_p
			and 	nr_seq_impedimento 	= nr_seq_impedimento_w;
		else
			delete	FROM san_doacao_impedimento
			where 	nr_seq_doacao 		= nr_seq_doacao_p
			and 	nr_seq_impedimento 	= nr_seq_impedimento_w;
		end if;
	end if;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
