SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_atualizar_imped_quest (nr_seq_doacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_impede_doacao_w	varchar(1);

C01 CURSOR FOR
	SELECT 	nr_sequencia
	from	san_questionario
	where	nr_seq_doacao = nr_seq_doacao_p;


BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select  CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_impede_doacao_w
		from	san_questionario a,
			san_impedimento x
		where	x.nr_sequencia 		= a.nr_seq_impedimento
		and	a.ie_resposta 		<> coalesce(x.ie_resposta_correta,'N')
		and 	(a.ie_resposta IS NOT NULL AND a.ie_resposta::text <> '')
		and	coalesce(x.ie_pergunta,'N')  = 'N'
		and	a.nr_seq_doacao 	= nr_seq_doacao_p
		and	a.nr_sequencia 		= nr_sequencia_w
		and	((x.ie_definitivo = 'S') or (x.ie_definitivo = 'I') or ((coalesce(a.dt_ocorrencia::text, '') = '') or (trunc(a.dt_ocorrencia + coalesce(x.nr_dias_inaptidao,0)) >= trunc(clock_timestamp()))));

		update	san_questionario
		set	ie_impede_doacao	=  ie_impede_doacao_w
		where	nr_sequencia		=  nr_sequencia_w;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
