CREATE OR REPLACE PROCEDURE san_atualizar_lote_producao ( nr_seq_producao_p bigint, nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_producao_w	bigint;


BEGIN

if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') and (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') and (nr_seq_lote_p > 0) then

	select	max(nr_sequencia)
	into STRICT	nr_seq_producao_w
	from	san_producao
	where	nr_seq_prod_origem = nr_seq_producao_p;

	if (nr_seq_producao_w IS NOT NULL AND nr_seq_producao_w::text <> '') then
		update	san_producao
		set	nr_seq_lote_fornec = nr_seq_lote_p
		where	nr_sequencia = nr_seq_producao_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
