CREATE OR REPLACE PROCEDURE san_atualizar_motivo_inaptidao ( nr_seq_doacao_p bigint, nr_seq_impedimento_p bigint, nr_seq_motivo_inaptidao_p text, dt_ocorrencia_p timestamp, nm_usuario_p text) AS $body$
BEGIN
 
IF (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') THEN 
 
	update	san_questionario 
	set	nr_seq_motivo_inaptidao	= nr_seq_motivo_inaptidao_p, 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp(), 
		dt_ocorrencia		= coalesce(dt_ocorrencia_p, dt_ocorrencia) 
	where	nr_seq_impedimento 	= nr_seq_impedimento_p 
	and	nr_seq_doacao		= nr_seq_doacao_p;
	 
	--SAN EVENTO - INAPTID√ÉO DO DOADOR 
	CALL executa_evento_hemoterapia( nm_usuario_p , nr_seq_doacao_p , 6);
	 
END IF;
 
COMMIT;
 
END;
$body$
