CREATE OR REPLACE PROCEDURE san_atualizar_motivo_troca ( NR_SEQ_RESERVA_P bigint, CD_MOTIVO_TROCA_P bigint, DS_OBSERVACAO_P text) AS $body$
BEGIN

if (nr_seq_reserva_p IS NOT NULL AND nr_seq_reserva_p::text <> '') then

	update	san_reserva
	set	ds_obs_cancelamento = ds_observacao_p,
		nr_seq_motivo_troca = cd_motivo_troca_p
	where	nr_sequencia = nr_seq_reserva_p;

end if;

commit;

end;
