SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_atualizar_numero_sangue (nr_seq_doacao_p bigint, nr_sangue_p text) AS $body$
DECLARE


qt_sangue_w	bigint;
nr_sangue_w	varchar(20);
qtd_w		integer := 0;


BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	nr_sangue_w := nr_sangue_p;

	select	count(*)
	into STRICT	qt_sangue_w
	from	san_doacao
	where	nr_sangue = nr_sangue_w
	and	nr_sequencia <> nr_seq_doacao_p;

	while(qt_sangue_w > 0) and (qtd_w < 100) loop
		begin

		nr_sangue_w := obter_numero_sangue_seq(coalesce(wheb_usuario_pck.get_cd_estabelecimento,1));

		select	count(*)
		into STRICT	qt_sangue_w
		from	san_doacao
		where	nr_sangue = nr_sangue_w
		and	nr_sequencia <> nr_seq_doacao_p;

		qtd_w := qtd_w + 1;
		end;
	end loop;

	if (qt_sangue_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(264180);
	end if;

	if (nr_sangue_w <= 0) then
		CALL gravar_log_tasy(99881,wheb_mensagem_pck.get_texto(803051)||': '||nr_seq_doacao_p||', '||wheb_mensagem_pck.get_texto(803052)||': '||nr_sangue_w,wheb_usuario_pck.get_nm_usuario);
	end if;

	update	san_doacao
	set	nr_sangue = nr_sangue_w
	where	nr_sequencia = nr_seq_doacao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
