SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_atualizar_prod_emprest ( nr_seq_producao_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then

	if (ie_opcao_p = 'I') then

		update 	san_producao
		set	dt_inicio_prod_emprestimo 	= clock_timestamp(),
			nm_usuario_ini_prod_emprestimo	= nm_usuario_p
		where 	nr_sequencia			= nr_seq_producao_p
		and 	coalesce(dt_inicio_prod_emprestimo::text, '') = '';
	else if (ie_opcao_p = 'D') then

		update 	san_producao
		set	dt_inicio_prod_emprestimo 	 = NULL,
			nm_usuario_ini_prod_emprestimo	 = NULL
		where 	nr_sequencia			= nr_seq_producao_p;
	else
		update 	san_producao
		set	dt_fim_prod_emprestimo		= clock_timestamp(),
			nm_usuario_fim_prod_emprestimo	= nm_usuario_p
		where 	nr_sequencia			= nr_seq_producao_p;
	end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
