CREATE OR REPLACE PROCEDURE san_atualizar_qt_atendida ( nr_seq_reserva_p bigint, nr_seq_item_p bigint, qt_volume_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_volume_w	double precision;


BEGIN
if (nr_seq_reserva_p IS NOT NULL AND nr_seq_reserva_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then

	select	coalesce(qt_volume_atend,0) + qt_volume_p
	into STRICT	qt_volume_w
	from	san_reserva_item
	where	nr_seq_reserva = nr_seq_reserva_p
	and	nr_seq_item = nr_seq_item_p;

	update	san_reserva_item
	set	qt_volume_atend = qt_volume_w
	where	nr_seq_reserva = nr_seq_reserva_p
	and	nr_seq_item = nr_seq_item_p;

end if;

commit;

end;
$body$
