CREATE OR REPLACE PROCEDURE san_atualizar_questionario_js ( ie_questionar_p text, ie_verificar_inaptidao_p text, ie_obter_dados_p text, nr_seq_doacao_p bigint, nr_seq_impedimento_p bigint, nr_seq_quest_p bigint, ie_resposta_p text, dt_ocorrencia_p timestamp, ds_observacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ds_pergunta_p INOUT text, ds_justificativa_p INOUT text, ie_resposta_correta_p INOUT text) AS $body$
DECLARE


ie_resp_questionario_w	varchar(1);


BEGIN

select	coalesce(ie_resposta_correta, 'N')
into STRICT	ie_resposta_correta_p
from	san_impedimento
where	nr_sequencia	= nr_seq_impedimento_p;


if (ie_questionar_p = 'S') then
	begin
	select	coalesce(ie_resp_questionario, 'N')
	into STRICT	ie_resp_questionario_w
	from	san_doacao
	where	nr_sequencia = nr_seq_doacao_p;

	if (ie_resp_questionario_w = 'S') then
		begin
		ds_pergunta_p	:= obter_texto_tasy(81339, wheb_usuario_pck.get_nr_seq_idioma);
		ds_justificativa_p	:= san_obter_justificativa_quest(nr_seq_doacao_p, nr_seq_impedimento_p, cd_estabelecimento_p);
		end;
	else
		begin
		CALL san_atualizar_questionario(
			nr_seq_quest_p,
			ie_resposta_p,
			dt_ocorrencia_p,
			ds_observacao_p,
			nm_usuario_p);
		end;
	end if;
	end;
else
	begin
	CALL san_atualizar_questionario(
		nr_seq_quest_p,
		ie_resposta_p,
		dt_ocorrencia_p,
		ds_observacao_p,
		nm_usuario_p);
	end;
end if;

if (ie_obter_dados_p = 'S') then
	begin
	ie_resp_questionario_w	:= san_respondeu_questionario(nr_seq_doacao_p, nr_seq_impedimento_p, cd_estabelecimento_p);

	if (ie_resp_questionario_w = 'S') then
		begin
		ds_pergunta_p	:= obter_texto_tasy(94054, wheb_usuario_pck.get_nr_seq_idioma);
		ds_justificativa_p	:= san_obter_justificativa_quest(nr_seq_doacao_p, nr_seq_impedimento_p, cd_estabelecimento_p);
		end;
	end if;
	end;
end if;

end;
$body$
