CREATE OR REPLACE PROCEDURE san_atualizar_result_exame_hem ( nr_seq_exame_p bigint, nr_seq_exame_lote_p bigint, ds_resultado_atual_p text, nm_usuario_p text) AS $body$
DECLARE


cd_chave_w	varchar(255);
cd_chave_orig_w	varchar(255);
ds_chave_w	varchar(255);
ie_atualizar_w	varchar(1) := 'N';
cd_tipo_resultado_w	bigint;
ie_cursor_w	varchar(1):= 'N';

c01 CURSOR FOR
SELECT	ds_valor cd_chave,
	ds_valor ds_chave
from 	san_exame_valor
where 	nr_seq_exame = nr_seq_exame_p
and 	0 = cd_tipo_resultado_w

union

select	ds_valor_dominio cd_chave,
	ds_valor_dominio ds_chave
from 	valor_dominio
where 	cd_dominio = cd_tipo_resultado_w;


BEGIN

if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') and (nr_seq_exame_lote_p IS NOT NULL AND nr_seq_exame_lote_p::text <> '') and (ds_resultado_atual_p IS NOT NULL AND ds_resultado_atual_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	max(cd_tipo_resultado)
	into STRICT	cd_tipo_resultado_w
	from	san_exame
	where	nr_sequencia = nr_seq_exame_p;

	open c01;
	loop
	fetch c01 into	cd_chave_w,
			ds_chave_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		if (ie_cursor_w = 'N') then
			cd_chave_orig_w := cd_chave_w;
		end if;

		ie_cursor_w := 'S';

		if (ie_atualizar_w = 'S') then
			begin

			update	san_exame_realizado
			set	ds_resultado = ds_chave_w,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	nr_seq_exame = nr_seq_exame_p
			and	nr_seq_exame_lote = nr_seq_exame_lote_p;
			commit;

			ie_atualizar_w := 'N';
			end;
		end if;

		if (cd_chave_w = ds_resultado_atual_p) then
			begin
			ie_atualizar_w := 'S';
			end;
		end if;

		end;
	end loop;
	close c01;

	if (ie_atualizar_w = 'S') and (ie_cursor_w =  'S') then

			update	san_exame_realizado
			set	ds_resultado = cd_chave_orig_w,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	nr_seq_exame = nr_seq_exame_p
			and	nr_seq_exame_lote = nr_seq_exame_lote_p;
			commit;
	end if;

	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
