SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_atualizar_status_doacao ( nr_sequencia_p text, nm_usuario_p text, ie_status_p bigint, ie_opcao_p text default null) AS $body$
DECLARE

ie_status_w	bigint	:= ie_status_p;

BEGIN
	 
if (ie_opcao_p	= '2AMOSTRA') then 
	ie_status_w	:= 5;
	 
	if (OBTER_PARAM_USUARIO_LOGADO(450,324) = 'S') then 
		ie_status_w	:= 8;
	end if;
 
	 
end if;
	 
	 
if (ie_status_w in (1,7)) then 
	 
	UPDATE 	san_doacao 
	SET 	ie_status  	= ie_status_w, 
		dt_atualizacao 	= clock_timestamp(), 
		dt_triagem	= clock_timestamp(), 
		nm_usuario 	= nm_usuario_p 
	WHERE 	nr_sequencia 	= nr_sequencia_p;
 
	if (ie_status_w = 1) then	 
		--SAN EVENTO- LIBERAR PARA A COLETA 
		CALL executa_evento_hemoterapia( nm_usuario_p , nr_sequencia_p , 2);
	end if;
	 
else 
 
	UPDATE 	san_doacao 
	SET 	ie_status  	= ie_status_w, 
			dt_atualizacao 	= clock_timestamp(), 
			nm_usuario 	= nm_usuario_p 
	WHERE 	nr_sequencia 	= nr_sequencia_p;
 
end if;
 
COMMIT;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
