CREATE OR REPLACE PROCEDURE san_atualiza_atend_sangria ( nr_atendimento_p bigint, nr_seq_sangria_p bigint) AS $body$
DECLARE

cd_medico_w	bigint;


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	select	max(cd_medico_resp) 
	into STRICT	cd_medico_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
end if;
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') and (nr_seq_sangria_p IS NOT NULL AND nr_seq_sangria_p::text <> '') then 
	update	san_sangria_terapeutica 
	set	nr_atendimento = nr_atendimento_p, 
		cd_medico = cd_medico_w 
	where	nr_sequencia = nr_seq_sangria_p;	
end if;		
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
