CREATE OR REPLACE PROCEDURE san_atualiza_imped_sv ( nr_seq_doacao_p san_doacao.nr_sequencia%type, nr_dias_regra_p bigint) AS $body$
DECLARE


dt_inaptidao_w timestamp;


BEGIN

if (nr_dias_regra_p IS NOT NULL AND nr_dias_regra_p::text <> '') then
   dt_inaptidao_w := (clock_timestamp() + nr_dias_regra_p);
end if;

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	insert into san_inapto_sinal_vital(
	 	 	nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			dt_liberacao,
			nr_seq_doacao,
			ie_inapto_definitivo,
			dt_ocorrencia)
		values (	nextval('san_inapto_sinal_vital_seq'),
			wheb_usuario_pck.get_nm_usuario,
			clock_timestamp(),
			wheb_usuario_pck.get_nm_usuario,
			clock_timestamp(),
			dt_inaptidao_w,
			nr_seq_doacao_p,
			'N',
			clock_timestamp());
end if;

commit;

end;
$body$
