CREATE OR REPLACE PROCEDURE san_atualiza_inutil_bolsa_js ( nr_seq_producao_p text, nr_seq_inutil_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE

nr_sequencia_w		bigint;
nr_sequencias_w		varchar(2000);
nr_pos_virgula_w	bigint;
nr_seq_producao_w	bigint;


BEGIN 
 
if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then 
	begin 
	nr_sequencias_w	:= nr_seq_producao_p;
	while(nr_sequencias_w IS NOT NULL AND nr_sequencias_w::text <> '') loop 
		begin 
		nr_pos_virgula_w	:= position(',' in nr_sequencias_w);
		if (nr_pos_virgula_w > 0) then 
			begin 
			nr_sequencia_w	:= (substr(nr_sequencias_w,1,nr_pos_virgula_w-1))::numeric;
			nr_sequencias_w	:= substr(nr_sequencias_w,nr_pos_virgula_w+1,length(nr_sequencias_w));
			if ( nr_sequencia_w > 0) then 
				begin 
				CALL san_atualiza_inutil_bolsa( nr_sequencia_w, nr_seq_inutil_p, cd_estabelecimento_p );
				end;
			end if;
		end;
		end if;
	end;
	end loop;
	 
	 
end;
end if;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
