CREATE OR REPLACE PROCEDURE san_bloquear_hemo_trali ( nr_seq_producao_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_doacao_w		bigint;
cd_pessoa_fisica_w	varchar(10);
ie_bloqueio_w		varchar(1);
ie_tipo_bloqueio_w	varchar(1);
ie_opcao_w		varchar(1);
nr_seq_derivado_w	bigint;


C01 CURSOR FOR
	SELECT 	b.cd_pessoa_fisica,
		b.nr_sequencia,
		c.ie_opcao,
		a.nr_seq_derivado
	FROM	san_producao a,
		san_doacao b,
		san_regra_trali c
	WHERE	a.nr_seq_doacao 	= b.nr_sequencia
	AND	c.nr_seq_derivado 	= a.nr_seq_derivado
	AND	a.nr_sequencia		= nr_seq_producao_p
	order by 1;


BEGIN

if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		cd_pessoa_fisica_w,
		nr_seq_doacao_w,
		ie_opcao_w,
		nr_seq_derivado_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		if (ie_opcao_w = 'M') then
			ie_bloqueio_w := obter_se_doadora_multigesta(nr_seq_doacao_w);
			if (ie_bloqueio_w = 'S') then
				ie_tipo_bloqueio_w := 'M';
			end if;
		elsif (ie_opcao_w = 'T') then
			ie_bloqueio_w := san_obter_se_doador_trali(cd_pessoa_fisica_w);
			if (ie_bloqueio_w = 'S') then
				ie_tipo_bloqueio_w := 'T';
			end if;
		else
			ie_bloqueio_w := obter_se_doadora_multigesta( nr_seq_doacao_w);
			if (ie_bloqueio_w = 'S') then
				ie_tipo_bloqueio_w := 'M';
			else
				ie_bloqueio_w := san_obter_se_doador_trali(cd_pessoa_fisica_w);
				if (ie_bloqueio_w = 'S') then
					ie_tipo_bloqueio_w := 'T';
				end if;
			end if;
		end if;

		if (ie_tipo_bloqueio_w IS NOT NULL AND ie_tipo_bloqueio_w::text <> '') then

			update	san_producao
			set	ie_tipo_bloqueio 	= ie_tipo_bloqueio_w,
				dt_bloqueio    		= clock_timestamp(),
				nm_usuario_bloqueio  	= nm_usuario_p
			where 	nr_sequencia		= nr_seq_producao_p
			and	nr_seq_derivado		= nr_seq_derivado_w;
		end if;
	end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
