CREATE OR REPLACE PROCEDURE san_cancelar_transfusao ( nr_seq_transfusao_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_transfusao_p IS NOT NULL AND nr_seq_transfusao_p::text <> '') then

	update	san_transfusao
	set	dt_cancelamento		= clock_timestamp(),
		nm_usuario_cancelamento = nm_usuario_p,
		ie_status		= 'C'
	where	nr_sequencia	= nr_seq_transfusao_p;

	commit;

end if;

end;
$body$
