CREATE OR REPLACE PROCEDURE san_cancela_reserva_js ( ie_consistir_p text, nr_atendimento_p bigint, cd_setor_atendimento_p bigint, nr_seq_reserva_p bigint, nm_usuario_p text, ds_pergunta_p INOUT text) AS $body$
DECLARE


ie_possui_proc_w	varchar(1);


BEGIN

if (ie_consistir_p = 'S') then
	begin
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		begin
		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_possui_proc_w
		from	procedimento_paciente
		where	nr_atendimento	= nr_atendimento_p
		and	cd_setor_atendimento = cd_setor_atendimento_p
		and	ds_observacao	like '%' || nr_seq_reserva_p || '%';

		if (ie_possui_proc_w = 'S') then
			ds_pergunta_p	:= obter_texto_tasy(83823, wheb_usuario_pck.get_nr_seq_idioma);
		end if;
		end;
	end if;
	end;
end if;

if (ie_consistir_p = 'N') or (coalesce(ds_pergunta_p::text, '') = '') then
	CALL san_cancela_reserva(nr_seq_reserva_p, nm_usuario_p);
end if;

end;
$body$
