SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_cancela_reserva (nr_seq_reserva_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_status_w	varchar(15);


BEGIN

SELECT	MAX(ie_status)
INTO STRICT	ie_status_w
FROM	san_reserva
WHERE 	nr_sequencia = nr_seq_reserva_p;

IF (ie_status_w = 'R') OR (ie_status_w = 'S') OR (ie_status_w = 'L') THEN

	UPDATE 	san_reserva
	SET 	ie_status = 'C',
		dt_cancelamento = clock_timestamp(),
		nm_usuario_cancelamento = nm_usuario_p
	WHERE 	nr_sequencia = nr_seq_reserva_p;

	UPDATE san_reserva_prod
	SET  ie_status = 'N'
	WHERE  nr_seq_reserva = nr_seq_reserva_p;

END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
