SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_consiste_bolsa_direcionada (sql_item_p text, cd_pessoa_receptor_p text, nm_usuario_p text, ds_item_retorno_p INOUT text, ds_retorno_p INOUT text) AS $body$
DECLARE


lista_producao_w		dbms_sql.varchar2_table;
nr_seq_doacao_w			bigint;
nr_seq_tipo_doacao_w		bigint;
ds_derivado_w			varchar(50);
nr_seq_producao_w		bigint;
nr_seq_prod_incons_w		varchar(2000);
cd_pessoa_dest_w		varchar(15);
qt_registro_w			integer;

BEGIN

ds_retorno_p := '';
lista_producao_w := obter_lista_string(sql_item_p, ',');

for	i in lista_producao_w.first..lista_producao_w.last loop
	nr_seq_producao_w := (lista_producao_w(i))::numeric;

	if (coalesce(nr_seq_producao_w,0) > 0) then

		select	max(nr_seq_doacao),
			max(substr(obter_desc_san_derivado(nr_seq_derivado),1,255))
		into STRICT	nr_seq_doacao_w,
			ds_derivado_w
		from	san_producao
		where	nr_sequencia = nr_seq_producao_w
		and	coalesce(dt_liberacao_direc::text, '') = '';

		if (coalesce(nr_seq_doacao_w, 0) > 0) then

			select	max(nr_seq_tipo),
				max(cd_pessoa_dest)
			into STRICT	nr_seq_tipo_doacao_w,
				cd_pessoa_dest_w
			from	san_doacao
			where	nr_sequencia = nr_seq_doacao_w;

			select	count(*)
			into STRICT	qt_registro_w
			from	san_tipo_doacao
			where	nr_sequencia		= nr_seq_tipo_doacao_w
			and	ie_classif_doacao 	= 'D'
			and	cd_pessoa_dest_w	<> cd_pessoa_receptor_p;

			if (qt_registro_w > 0) then

				select	substr(obter_desc_expressao(688456),1,255)|| chr(10)||chr(13)||ds_derivado_w
				into STRICT	ds_retorno_p
				;

				nr_seq_prod_incons_w	:= nr_seq_prod_incons_w||nr_seq_producao_w||',';

			end if;
		end if;
	end if;
end loop;

ds_item_retorno_p	:= nr_seq_prod_incons_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
