CREATE OR REPLACE PROCEDURE san_consiste_cad_novo_hemocomp (nr_seq_doacao_p bigint) AS $body$
DECLARE

ie_aborta_w	varchar(1);

BEGIN
if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	select	CASE WHEN coalesce(x.dt_fim_prod_doacao::text, '') = '' THEN 'N'  ELSE 'S' END
	into STRICT	ie_aborta_w
	from	san_doacao x
	where	x.nr_sequencia	= nr_seq_doacao_p;

	if (ie_aborta_w = 'S') then
		--A produção da doação já está finalizada, não é possível cadadastrar hemocomponentes para esta doação!
		--Favor  verificar.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(115044);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
