CREATE OR REPLACE PROCEDURE san_cons_sinais_vitais_tri (qt_quantidade_ps_p bigint, qt_quantidade_pd_p bigint, qt_quantidade_p_p bigint, qt_quantidade_t_p bigint, qt_quantidade_v_p bigint, qt_quantidade_f_p bigint, ie_triagem text, ds_mensagem_p INOUT text) AS $body$
DECLARE


qt_minima_w	double precision;
qt_maxima_w	double precision;
ds_mensagem_w	varchar(255);
ie_sinal_vital_w varchar(3);

C001 CURSOR FOR
SELECT	coalesce(qt_minimo,0),
		coalesce(qt_maximo,9999),
		ie_sinal_vital
from   	san_regra_sinal_vital
where	ie_situacao 	= 'A';

/*
PS - PA Sistólica
PD - PA Diastólica
P - Pulso
T - Temperatura
V - Volume
*/
BEGIN

ds_mensagem_w:= '';

open c001;
	loop
		fetch c001 into 	qt_minima_w,
							qt_maxima_w,
							ie_sinal_vital_w;
		EXIT WHEN NOT FOUND; /* apply on c001 */

			if('PS' = ie_sinal_vital_w AND qt_quantidade_ps_p IS NOT NULL AND qt_quantidade_ps_p::text <> '') then
				if ((qt_quantidade_ps_p < qt_minima_w) or (qt_quantidade_ps_p > qt_maxima_w)) then
					ds_mensagem_w := ds_mensagem_w ||', '|| wheb_mensagem_pck.get_Texto(285000);
				end if;
			end if;

			if('PD' = ie_sinal_vital_w AND qt_quantidade_pd_p IS NOT NULL AND qt_quantidade_pd_p::text <> '') then
				if ((qt_quantidade_pd_p < qt_minima_w) or (qt_quantidade_pd_p > qt_maxima_w)) then
					ds_mensagem_w := ds_mensagem_w ||', '|| wheb_mensagem_pck.get_Texto(285001);
				end if;
			end if;

			if('P' = ie_sinal_vital_w AND qt_quantidade_p_p IS NOT NULL AND qt_quantidade_p_p::text <> '') then
				if ((qt_quantidade_p_p < qt_minima_w) or (qt_quantidade_p_p > qt_maxima_w)) then
					ds_mensagem_w := ds_mensagem_w ||', '|| wheb_mensagem_pck.get_Texto(285002);
				end if;
			end if;

			if('T' = ie_sinal_vital_w AND qt_quantidade_t_p IS NOT NULL AND qt_quantidade_t_p::text <> '') then
				if ((qt_quantidade_t_p < qt_minima_w) or (qt_quantidade_t_p > qt_maxima_w)) then
					ds_mensagem_w := ds_mensagem_w ||', '|| wheb_mensagem_pck.get_Texto(285003);
				end if;
			end if;

			if(('V' = ie_sinal_vital_w) and (qt_quantidade_v_p IS NOT NULL AND qt_quantidade_v_p::text <> '') and (ie_triagem = 'T')) then
				if ((qt_quantidade_v_p < qt_minima_w) or (qt_quantidade_v_p > qt_maxima_w)) then
					ds_mensagem_w := ds_mensagem_w ||', '|| wheb_mensagem_pck.get_Texto(285004);
				end if;
			end if;

			if('F' = ie_sinal_vital_w AND qt_quantidade_f_p IS NOT NULL AND qt_quantidade_f_p::text <> '') then
				if ((qt_quantidade_f_p < qt_minima_w) or (qt_quantidade_f_p > qt_maxima_w)) then
					ds_mensagem_w := ds_mensagem_w ||', '|| wheb_mensagem_pck.get_Texto(285005);
				end if;
			end if;


		end loop;

	ds_mensagem_p:= ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
