CREATE OR REPLACE PROCEDURE san_definir_leucometria_padrao ( nr_seq_leucometria_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_producao_w		bigint;

BEGIN
if (nr_seq_leucometria_p IS NOT NULL AND nr_seq_leucometria_p::text <> '') then

	SELECT 	nr_seq_producao
	INTO STRICT	nr_seq_producao_w
	FROM   	san_leucometria
	WHERE  	nr_sequencia = nr_seq_leucometria_p;

	update 	san_leucometria
	set 	ie_leucometria_padrao = 'S',
		nm_usuario 		= nm_usuario_p,
		dt_atualizacao 		= clock_timestamp()
	where 	nr_sequencia 		= nr_seq_leucometria_p
	and	nr_seq_producao		= nr_seq_producao_w;

	update 	san_leucometria
	set 	ie_leucometria_padrao 	= 'N',
		nm_usuario 		=  nm_usuario_p,
		dt_atualizacao 		=  clock_timestamp()
	where 	((ie_leucometria_padrao = 'S') or (coalesce(ie_leucometria_padrao::text, '') = ''))
	and	nr_seq_producao		=  nr_seq_producao_w
	and	nr_sequencia 		<> nr_seq_leucometria_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
