SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_deletar_emprest_saida ( nr_seq_emprestimo_p bigint, nr_seq_emp_saida_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text, ie_mostrar_erro_p text default null) AS $body$
DECLARE


ie_possui_emprestimos_w	varchar(1);
ie_permite_alterar_transf_w	varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_possui_emprestimos_w
from	san_emprestimo
where	ie_entrada_saida	= 'S'
and	(dt_fechamento IS NOT NULL AND dt_fechamento::text <> '')
and	nr_sequencia	= nr_seq_emprestimo_p;

if (ie_possui_emprestimos_w = 'S') then
	ds_erro_p	:= obter_texto_tasy(92911, wheb_usuario_pck.get_nr_seq_idioma);
else
	begin
	/* Hemoterapia - Parâmetro [60] - Permite atualizar dados da transfusão */

	ie_permite_alterar_transf_w := obter_param_usuario(450, 60, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_alterar_transf_w);

	if (ie_permite_alterar_transf_w = 'N') then
		ds_erro_p	:= obter_texto_tasy(92916, wheb_usuario_pck.get_nr_seq_idioma);
	else
		begin
		update	san_producao
		set	nr_seq_emp_saida	 = NULL
		where	nr_sequencia	= nr_seq_emp_saida_p;

		commit;
		end;
	end if;
	end;
end if;

if (ie_mostrar_erro_p	= 'S')	and (ds_erro_p IS NOT NULL AND ds_erro_p::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(ds_erro_p);
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
