SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE san_deletar_transfusao ( ie_opcao_p text, nr_seq_transfusao_p bigint, nr_seq_reserva_p bigint, nr_seq_exame_lote_p bigint, ie_status_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


/*	ie_opcao_p
	T - Transfusao
	H - Hemocomponente	*/
ie_permite_alterar_transf_w	varchar(1);
ie_perm_excluir_hemo_w		varchar(1);
qt_data_lib_w 			bigint := 0;


BEGIN

if (ie_opcao_p = 'T') then
	begin
		if (nr_seq_reserva_p IS NOT NULL AND nr_seq_reserva_p::text <> '') then
			begin
				update	san_exame_lote
				set	nr_seq_transfusao  = NULL
				where	nr_seq_transfusao = nr_seq_transfusao_p;
			end;
		end if;
		
		select	count(c.dt_liberacao) qt_data_lib
			into STRICT qt_data_lib_w
		from	san_transfusao a,
			san_exame_lote b,
			san_exame_realizado c
		where	a.nr_sequencia = b.nr_seq_transfusao
		and	b.nr_sequencia = c.nr_seq_exame_lote
		and	a.nr_sequencia = nr_seq_transfusao_p;
	
		if ( qt_data_lib_w > 0 ) then
			ds_erro_p	:= obter_texto_tasy(316586, wheb_usuario_pck.get_nr_seq_idioma);
		end if;
			
		if ( coalesce(ds_erro_p::text, '') = '' ) then
			update	san_producao
			set	nr_seq_transfusao	 = NULL
			where	nr_seq_transfusao	= nr_seq_transfusao_p;
		
			delete	from san_transfusao
			where	nr_sequencia	= nr_seq_transfusao_p;
		
			commit;
		end if;
	end;

elsif (ie_opcao_p = 'H') then
	begin
		/* Hemoterapia - Parametro [60] - Permite atualizar dados da transfusao */

		ie_permite_alterar_transf_w := obter_param_usuario(450, 60, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_alterar_transf_w);
	
		if (ie_permite_alterar_transf_w = 'N') then
			ds_erro_p	:= obter_texto_tasy(92916, wheb_usuario_pck.get_nr_seq_idioma);
		elsif (ie_status_p = 'F') then
			begin
				/* Hemoterapia - Parametro [85] - Permite excluir hemocomponente apos finalizada a transfusao */

				ie_perm_excluir_hemo_w := obter_param_usuario(450, 85, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_perm_excluir_hemo_w);
		
				if (ie_perm_excluir_hemo_w = 'S') then
					ds_erro_p	:= obter_texto_tasy(92944, wheb_usuario_pck.get_nr_seq_idioma);
				end if;
			end;
		end if;
	
		if (coalesce(ds_erro_p::text, '') = '') then
			begin
				update	san_producao
				set	nr_seq_transfusao	 = NULL
				where	nr_sequencia	= nr_seq_transfusao_p;
		
				commit;
			end;
		end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
