CREATE OR REPLACE PROCEDURE san_desfazer_fim_prod_doacao ( nr_seq_doacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_aborta_w	varchar(1);
ie_existe_producao_w	varchar(1);


BEGIN
ie_aborta_w	:= 'S';

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_existe_producao_w
	from	san_producao
	where	nr_seq_doacao = nr_seq_doacao_p;

	if (ie_existe_producao_w = 'N') then
		ie_aborta_w := 'N';
	else
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_aborta_w
		from	san_producao
		where	nr_seq_doacao = nr_seq_doacao_p
		and	coalesce(nr_seq_transfusao::text, '') = ''
		and	coalesce(nr_seq_reserva::text, '') = ''
		and	coalesce(nr_seq_propaci::text, '') = ''
		and	coalesce(nr_seq_motivo_bloqueio::text, '') = ''
		and	coalesce(nr_seq_inutil::text, '') = ''
		and	coalesce(nr_seq_emp_saida::text, '') = '';
	end if;

	if (ie_aborta_w = 'S') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(180701);
	elsif (ie_aborta_w = 'N') then
		update	san_doacao
		set	dt_fim_prod_doacao	 = NULL,
			nm_usuario_fim_prod	 = NULL
		where	nr_sequencia		= nr_seq_doacao_p;

	/*insert into logxxxxxx_tasy (	cd_log,
				ds_log,
				dt_atualizacao,
				nm_usuario)
		values       (	88723,
				'O usuário = '||nm_usuario_p||' /alterou a seq doação = '||nr_seq_doacao_p||' /no dia = '||sysdate,
				sysdate,
				'tasy');*/
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
