CREATE OR REPLACE PROCEDURE san_desfaz_lavar_hemo (nr_seq_producao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_lavado_w	varchar(1);


BEGIN

select      max(ie_lavado)
into STRICT	ie_lavado_w
from 	san_producao
where 	nr_sequencia = nr_seq_producao_p;

if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') and (ie_lavado_w IS NOT NULL AND ie_lavado_w::text <> '') then

	delete from san_producao_lavagem
	where  nr_seq_producao = nr_seq_producao_p;

	update	san_producao
	set	ie_lavado 		= 	'N',
		nm_usuario_lavado	=	nm_usuario_p
	where	nr_sequencia		=	nr_seq_producao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
