CREATE OR REPLACE PROCEDURE san_doacao_exame_bp_js ( ie_auto_exclusao_p san_doacao.ie_auto_exclusao%type, dt_auto_exclusao_p san_doacao.dt_auto_exclusao%type, ds_observacao_p san_doacao.ds_observacao%type, nr_sequencia_p san_doacao.nr_sequencia%type, nm_usuario_p san_doacao.nm_usuario%type) AS $body$
BEGIN
