CREATE OR REPLACE PROCEDURE san_exame_realizado_afp_js ( nr_seq_exame_p bigint, nr_seq_exame_lote_p bigint, cd_pessoa_fisica_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_exame_tipo_w	bigint;
nr_seq_exame_rh_w	bigint;
ds_resultado_w		varchar(255);
nr_seq_exame_sub_abo_w	san_parametro.nr_seq_exame_sub_abo%type;
nr_seq_subgrupo_w	subtipo_sanguineo.nr_sequencia%type;


BEGIN

select 	nr_seq_exame_rh,
	nr_seq_exame_tipo,
	nr_seq_exame_sub_abo
into STRICT	nr_seq_exame_rh_w,
	nr_seq_exame_tipo_w,
	nr_seq_exame_sub_abo_w
from 	san_parametro
where  	cd_estabelecimento = cd_estabelecimento_p;

select 	ds_resultado
into STRICT	ds_resultado_w
from 	san_exame_realizado
where  	nr_seq_exame_lote = nr_seq_exame_lote_p
and    	nr_Seq_exame = nr_seq_exame_p;

if (nr_seq_exame_p = nr_seq_exame_tipo_w
	and ds_resultado_w <> '') then

	update 	pessoa_fisica
	set 	ie_tipo_sangue = obter_valor_dominio(1173, ds_resultado_w)
	where  	cd_pessoa_fisica = cd_pessoa_fisica_p;

end if;

if (nr_seq_exame_p = nr_seq_exame_rh_w
	and ds_resultado_w <> '') then

	if (upper(ds_resultado_w) = 'NEGATIVO'
		or upper(ds_resultado_w) = 'NEG') then
		ds_resultado_w := '-';
	elsif (upper(ds_resultado_w) = 'POSITIVO'
		or upper(ds_resultado_w) = 'POS') then
		ds_resultado_w := '+';
	end if;

	update 	pessoa_fisica
	set 	ie_fator_rh = ds_resultado_w
	where  	cd_pessoa_fisica = cd_pessoa_fisica_p;

end if;

if (nr_seq_exame_p = nr_seq_exame_sub_abo_w and ds_resultado_w <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_subgrupo_w
	from	subtipo_sanguineo
	where	ds_subtipo = substr(ds_resultado_w, 1, 20);

	if (nr_seq_subgrupo_w IS NOT NULL AND nr_seq_subgrupo_w::text <> '') then
		update 	pessoa_fisica
		set 	ie_subtipo_sanguineo = nr_seq_subgrupo_w
		where  	cd_pessoa_fisica = cd_pessoa_fisica_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
