CREATE OR REPLACE PROCEDURE san_excluir_exame_nat_emp ( nr_sequencia_p text, nm_usuario_p text) AS $body$
DECLARE




nr_seq_lote_doacao_w	bigint;
nr_seq_exame_w		bigint;

C01 CURSOR FOR
	SELECT	b.nr_seq_exame_lote,
		b.nr_seq_exame
	from  	san_exame_lote a,
		san_exame_realizado b,
		san_exame c
	where	a.nr_sequencia = b.nr_Seq_exame_lote
	and	b.nr_seq_exame = c.nr_sequencia
	and	a.nr_seq_producao = nr_sequencia_p
	and	coalesce(b.dt_liberacao::text, '') = ''
	and	coalesce(b.ds_resultado::text, '') = ''
	and	c.ie_tipo_exame = 4;

BEGIN


open C01;
		loop
		fetch C01 	into
				nr_seq_lote_doacao_w,
				nr_seq_exame_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			delete	FROM san_exame_realizado
			where	nr_seq_exame_lote 	= nr_seq_lote_doacao_w
			and	nr_seq_exame 		= nr_seq_exame_w
			and	coalesce(dt_liberacao::text, '') = ''
			and	coalesce(ds_resultado::text, '') = '';

			CALL gravar_log_tasy(1254, wheb_mensagem_pck.get_texto(799326) || ' - ' || wheb_mensagem_pck.get_texto(791960) ||': '||nr_seq_lote_doacao_w||' - '||nr_seq_exame_w||' - '||nm_usuario_p||' - '||to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss'),nm_usuario_p);

			end;
		end loop;
		close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
