CREATE OR REPLACE PROCEDURE san_excluir_hemoc_transf ( nr_seq_producao_p bigint, nr_seq_transfusao_p bigint, nm_usuario_p text) AS $body$
BEGIN
if	((coalesce(nr_seq_producao_p,0) > 0) and (coalesce(nr_seq_transfusao_p,0) > 0)) then
	begin

	delete	FROM san_exame_teste_realizado
	where	nr_seq_exame_lote in (
					SELECT	nr_sequencia
					from	san_exame_lote
					where	nr_seq_producao	= nr_seq_producao_p
					and	ie_origem	= 'T'
					and	coalesce(nr_seq_res_prod::text, '') = '');

	update	san_producao
	set	nr_seq_transfusao	 = NULL
	where	nr_sequencia		= nr_seq_producao_p
	and	nr_seq_transfusao	= nr_seq_transfusao_p;
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
